import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import axios from 'axios';
import PokeList from './PokeList';
class PokeContainer extends React.Component {
    constructor() {
        super();
        this.state = {
            pokemons: []
        };
    }

    async componentDidMount() {
        await axios.get('https://pokeapi.co/api/v2/pokemon')
            .then(response => {
                console.log(response.data.results);
                this.setState({
                    pokemons: response.data.results,
                })
            })
            .catch(error => { console.log(error) });
    }
    render() {
        //let { pokemons = [] } = this.state;
        let { pokemons = [] } = this.array
        return (
            <div>
                <Container>
                    <Row>
                        {
                            pokemons.map((pokemon, index) => {
                                let urlImage = 'https://github.com/PokeAPI/sprites/blob/master/sprites/pokemon/other-sprites/official-artwork/';
                                let i = pokemon.url.split('/')[pokemon.url.split('/').length - 2];
                                return (
                                    <Col xs={3} key={index} >
                                        <PokeList  name={pokemon.name} image={`${urlImage}${i}.png?raw=true`} url={pokemon.url} />
                                    </Col>
                                );
                            })
                        }
                    </Row>

                </Container>
            </div>
        );
    }
}

export default PokeContainer;
