import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import axios from 'axios';
import PokeList from './PokeList';
import PokeNav from './PokeNav';

class PokeContainer extends React.Component {
    constructor() {
        super();
        this.state = {
            pokemons: [],
        };
    }

    async componentDidMount() {
        await axios.get('https://pokeapi.co/api/v2/pokemon')
            .then(response => {
                console.log(response.data.results);
                this.setState({
                    pokemons: response.data.results,
                })
            })
            .catch(error => { console.log(error) });
    }
    render() {
        //let { pokemons = [] } = this.state;
        let  pokemons = [
            {name:"bulbasaur",
            url:"https://pokeapi.co/api/v2/pokemon/1/"},
            {name:"ivysaur",
            url:"https://pokeapi.co/api/v2/pokemon/2/"},
            {name:"venusaur",
            url:"https://pokeapi.co/api/v2/pokemon/3/"},
            {name:"charmander",
            url:"https://pokeapi.co/api/v2/pokemon/4/"},
            {name:"charmeleon",
            url:"https://pokeapi.co/api/v2/pokemon/5/"},
            {name:"charizard",
            url:"https://pokeapi.co/api/v2/pokemon/6/"},
            {name:"squirtle",
            url:"https://pokeapi.co/api/v2/pokemon/7/"},
            {name:"wartortle",
            url:"https://pokeapi.co/api/v2/pokemon/8/"},
            {name:"blastoise",
            url:"https://pokeapi.co/api/v2/pokemon/9/"},
            {name:"caterpie",
            url:"https://pokeapi.co/api/v2/pokemon/10/"},
            {name:"metapod",
            url:"https://pokeapi.co/api/v2/pokemon/11/"},
            {name:"butterfree",
            url:"https://pokeapi.co/api/v2/pokemon/12/"},
        ];  
        return (
            <div>
                <PokeNav />
                <Container>
                    <Row>
                        {
                            pokemons.map((pokemon, index) => {
                                let urlImage = 'https://github.com/PokeAPI/sprites/blob/master/sprites/pokemon/other-sprites/official-artwork/';
                                let i = pokemon.url.split('/')[pokemon.url.split('/').length - 2];
                                return (
                                    <Col xs={3} key={index} >
                                        <PokeList  name={pokemon.name} image={`${urlImage}${i}.png?raw=true`} url={i} />
                                    </Col>
                                );
                            })
                        }
                    </Row>

                </Container>
            </div>
        );
    }
}

export default PokeContainer;
