import React from 'react';
import { Container, Row, Col, Card, OverlayTrigger } from 'react-bootstrap';
import axios from 'axios';

class PokeInfo extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            pokeinfo: [],
            name: this.props.match.params.name,
            id: this.props.match.params.id,
        };
        console.log(this.state);
    }

    async componentDidMount() {
        await axios.get(`https://pokeapi.co/api/v2/pokemon/${this.state.id}/`)
            .then(response => {
                console.log(response.data);
                this.setState({
                    pokeinfo: response.data,
                });
            })
            .catch(error => { console.log(error) });
    }

    render() {
        return (
            <Container className="my-4">
                <Row>
                    <Col xs={6} className="mx-auto">
                        <Card>
                            <Card.Header>
                                <h1>PokeInfo: {this.state.name}</h1>
                            </Card.Header>
                            <Card.Body>
                                <h1>PokeInfo: {this.state.name}</h1>
                                <p>URL: {this.state.id} </p>
                                <ButtonToolbar>
                                    {['top', 'right', 'bottom', 'left'].map(placement => (
                                        <OverlayTrigger
                                            key={placement}
                                            placement={placement}
                                            overlay={
                                                <Tooltip id={`tooltip-${placement}`}>
                                                    Tooltip on <strong>{placement}</strong>.
                                                </Tooltip>
                                            }
                                        >
                                            <Button variant="secondary">Tooltip on {placement}</Button>
                                        </OverlayTrigger>
                                    ))}
                                </ButtonToolbar>
                            </Card.Body>
                        </Card>
                    </Col>
                </Row>
            </Container>
        );
    }
}

export default PokeInfo;
