import React from 'react';
import { Container, Row, Col, Card, Accordion, Button } from 'react-bootstrap';
import axios from 'axios';
import PokeNav from './PokeNav';
import Pokeball from '../img/Pokeball.png';

class PokeInfo extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            pokeinfo: [],
            name: this.props.match.params.name,
            id: this.props.match.params.id,
        };
        console.log(this.state);
    }

    async componentDidMount() {
        await axios.get(`https://pokeapi.co/api/v2/pokemon/${this.state.id}/`)
            .then(response => {
                console.log(response.data);
                this.setState({
                    pokeinfo: response.data,
                });
            })
            .catch(error => { console.log(error) });
    }

    render() {
        let urlImage = `https://github.com/PokeAPI/sprites/blob/master/sprites/pokemon/other-sprites/official-artwork/${this.state.id}.`;
        return (
            <>
                <PokeNav />
                <Container className="my-4">
                    <Row>
                        <Col xs={8} className="mx-auto">
                            <Card>
                                <Row>
                                    <Col xs={6}>
                                        <Card.Body>
                                            <Card.Text className="text-center">
                                                <img src={urlImage} width="300px" alt="Pokeball" className="img-fluid" />
                                                <h1>{this.state.name}</h1>
                                                <p>ID: {this.state.id} </p>
                                            </Card.Text>
                                        </Card.Body>
                                    </Col>
                                    <Col xs={6}>
                                        <Accordion defaultActiveKey="">
                                            <Card>
                                                <Accordion.Toggle as={Button} variant="link" eventKey="0">
                                                    Habilities
                                                </Accordion.Toggle>
                                                <Accordion.Collapse eventKey="0">
                                                    <Card.Body>
                                                        Hello! I'm the body
                                                    </Card.Body>
                                                </Accordion.Collapse>
                                            </Card>
                                            <Card>
                                                <Accordion.Toggle as={Button} variant="link" eventKey="1">
                                                    Click me!
                                                </Accordion.Toggle>
                                                <Accordion.Collapse eventKey="1">
                                                    <Card.Body>Hello! I'm another body</Card.Body>
                                                </Accordion.Collapse>
                                            </Card>
                                        </Accordion>
                                    </Col>
                                </Row>
                            </Card>
                        </Col>
                    </Row>
                </Container>
            </>
        );
    }
}

export default PokeInfo;
